mapper.select:

select * 
from Users
where id = #{id} and password=#{password}

select * 
from StudentInfo
where ${category} like CONCAT('%',#{value},'%');

mapper.update:
update gradeHistory
set
	semester=#{semester}, credit=#{credit}, degree=#{degree},
	warnings=#{warnings}, modifiedBy=#{modifiedBy}
where
	tid = #{tid};

update StudentInfo
set
	profile = #{profile}
where
	pid = #{pid};

update activeHistory
set
	pid = #{pid},year = #{year},
	content = #{content},memo = #{memo}, modifiedBy = #{modifiedBy}
where
	tid = #{tid};

mapper.delete:
delete
from awardsHistory
where pid = #{pid} and tid = #{tid};

delete
from activeHistory
where pid = #{pid} and tid = #{tid};

delete
from gradeHistory
where pid = #{pid} and tid = #{tid};

delete
from holyHistory
where pid = #{pid} and tid = #{tid};

delete
from consultHistory
where pid = #{pid} and tid = #{tid};

delete
from absenceHistory
where pid = #{pid} and tid = #{tid};

delete
from grantHistory
where pid = #{pid} and tid = #{tid};

mapper.update:
update StudentInfo
set
	totalCredit = #{totalCredit}
where
	pid = #{pid};

update StudentInfo
set
	totalGradeWarning = #{totalGradeWarning}
where
	pid = #{pid};

update StudentInfo
